sql.where:
flag = true and status = true
 and code = #{code}


         and name= #{name}


            and name like #{likeName}





        and type_id is null
         and type_id = #{typeId}::uuid


 and recommend_state = #{recommendState} 
 and hot_state = #{hotState} 
 and show_state = #{showState} 
 and test_type = #{testType}
 and test_state = #{testState}
 and question_type = #{questionType}
 and id <> #{exceptId}::uuid

mapper.select:
select c.id, c.code, c.name, t.id type_id, t.name type_name, c.introduction, c.study_to_exam_state, c.image,
e.id teacher_id, e.full_name teacher_name, c.total_score, c.pass_score, c.test_type, c.test_state, c.test_amount,
c.duration, c.question_amount, c.question_type, c.single_amount, c.multi_amount, c.judge_amount, c.check_amount,
c.fill_amount, c.recommend_state, c.hot_state, c.show_state
from course c
left join course_type t on t.id = c.type_id
left join teacher e on e.id = c.teacher_id
where c.flag = true and c.status = true
 and c.code = #{code}


         and c.name= #{name}


            and c.name like #{likeName}



 and c.recommend_state = #{recommendState} 
 and c.hot_state = #{hotState} 
 and c.show_state = #{showState} 
 and c.test_type = #{testType} 
 and c.test_state = #{testState} 
 and c.question_type = #{questionType} 
 t.id = #{typeId} 
 e.id = #{teacherId} 
 and c.id <> #{exceptId}::uuid 
order by c.update_time desc
